import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import "./App.css";

const Home = () => {
  return (
    <>
      <h1>Home</h1>
    </>
  );
};

const About = () => {
  return (
    <>
      <h1>About</h1>
    </>
  );
};

const Contact = () => {
  return (
    <>
      <h1>Contact</h1>
    </>
  );
};

function App() {
  return (
    <>
      {/* header */}
      {/* navigation */}
      {/* body */}
      <Router>
        <Switch>
          <Route exact path="/" component={Home} />
          <Route path="/about" component={About} />
          <Route path="/contact" component={Contact} />
        </Switch>
      </Router>
      {/* <h2>body</h2> */}
      {/* footer */}
      {/* <h2>footer</h2> */}
    </>
  );
}

export default App;
